UPDATE JFD_Governments
SET FreeUnitClassType = 'UNITCLASS_JFD_GREAT_DIGNITARY'
WHERE Type = 'GOVERNMENT_JFD_MONARCHY'
AND EXISTS (SELECT * FROM JFD_GlobalUserSettings WHERE Type = 'JFD_RTP_CLAIMS_CORE' AND Value = 1);

UPDATE JFD_Governments
SET FreeUnitClassType = 'UNITCLASS_JFD_GREAT_MAGISTRATE'
WHERE Type = 'GOVERNMENT_JFD_REPUBLIC'
AND EXISTS (SELECT * FROM Resources WHERE Type = 'RESOURCE_MAGISTRATES')
AND EXISTS (SELECT * FROM JFD_GlobalUserSettings WHERE Type = 'JFD_RTP_CLAIMS_CORE' AND Value = 1);
